package MyApp;
use base qw(Wx::App);
use strict;
sub OnInit {
        my( $self ) = shift;
        Wx::InitAllImageHandlers();
        my $frame_1 = MyFrame->new();
        $self->SetTopWindow($frame_1);
        $frame_1->Show(1);
        return 1;
}
# end of class MyApp
