#!/usr/bin/env python2
import wx from wx import xrc
GUI_FILENAME = "test.xrc" GUI_MAINFRAME_NAME = "Main"
class MyApp(wx.App): def OnInit(self): self.res = xrc.XmlResource(GUI_FILENAME) self.frame = self.res.LoadFrame(None, GUI_MAINFRAME_NAME) self.frame.Show() return True
if __name__ == '__main__': app = MyApp() app.MainLoop()

